package graphqlutil

import (
	_ "embed"
	htmltemplate "html/template"
	"net/http"

	"github.com/authgear/authgear-server/pkg/util/httputil"
)

//go:embed graphiql.generated.html
var graphiqlHTMLString string

// graphiql.html is generated by running `make graphiql` at project root.
// Please do not edit it manually!

var graphiqlTemplate = htmltemplate.Must(htmltemplate.New("graphiql").Parse(graphiqlHTMLString))

type GraphiQL struct {
	IsPortal bool
	Title    string
}

func (g *GraphiQL) ServeHTTP(w http.ResponseWriter, r *http.Request) {
	w.Header().Set("Content-Type", "text/html; charset=utf-8")

	nonce := httputil.GetCSPNonce(r.Context())
	data := map[string]interface{}{
		"Title":    g.Title,
		"CSPNonce": nonce,
		"IsPortal": g.IsPortal,
	}

	err := graphiqlTemplate.Execute(w, data)
	if err != nil {
		panic(err)
	}
}
